/*==================[macros]================================================*/
#define ICU_GEN_MODULE_ID                   (122U)  /* Module ID */
#define ICU_GEN_VENDOR_ID                   (66U)   /* Vendor ID */
#define ICU_GEN_AR_RELEASE_MAJOR_VERSION    (4U)    /* Autosar Release Major Version */
#define ICU_GEN_AR_RELEASE_MINOR_VERSION    (2U)    /* Autosar Release Minor Version */
#define ICU_GEN_AR_RELEASE_REVISION_VERSION (2U)    /* Autosar Release Revision Version */
#define ICU_GEN_SW_MAJOR_VERSION            (1U)    /* Software Major Version */
#define ICU_GEN_SW_MINOR_VERSION            (16U)    /* Software Minor Version */
#define ICU_GEN_SW_PATCH_VERSION            (1U)    /* Software Patch Version */

/* Reference to configured DEM event to report "Hardware failure". If the reference is not configured the error shall not be reported. */
#define ICU_DEM_E_HW_ERROR                  (STD_ON)
#define ICU_INTERNAL_E_HW_ERROR             (DemConf_DemEventParameter_ICU_E_HARDWARE_ERROR)

/* Switches the Default Error Tracer (Det) detection and notification ON or OFF. */
#define ICU_DEV_ERROR_DETECT                (STD_ON)

/* User configured API defines */
/* Adds / removes the service Icu_DeInit() from the code. */
#define ICU_DE_INIT_API                     (STD_ON)

/* Adds / removes the service Icu_DisableWakeup() from the code. */
#define ICU_DISABLE_WAKEUP_API              (STD_ON)

/* Adds / removes all services related to the edge counting functionality as listed below, from the code: Icu_ResetEdgeCount(), Icu_EnableEdgeCount(), Icu_DisableEdgeCount(), Icu_GetEdgeNumbers(). */
#define ICU_EDGE_COUNT_API                  (STD_ON)

/* Adds / removes the services related to the edge detection functionality, from the code: Icu_EnableEdgeDetection() and Icu_DisableEdgeDetection(). */
#define ICU_EDGE_DETECT_API                 (STD_ON)

/* Adds / removes the service Icu_EnableWakeup() from the code. */
#define ICU_ENABLE_WAKEUP_API               (STD_ON)

/* Adds / removes the service Icu_GetDutyCycleValues() from the code. */
#define ICU_GET_DUTY_CYCLE_VALUES_API       (STD_ON)

/* Adds / removes the service Icu_GetInputState() from the code. */
#define ICU_GET_INPUT_STATE_API             (STD_ON)

/* Adds / removes the service Icu_GetTimeElapsed() from the code. */
#define ICU_GET_TIME_ELAPSED_API            (STD_ON)

/* Adds / removes the service Icu_GetVersionInfo() from the code. */
#define ICU_GET_VERSION_INFO_API            (STD_ON)

/* Adds / removes the service Icu_SetMode() from the code. */
#define ICU_SET_MODE_API                    (STD_ON)

/* Adds / removes the services Icu_StartSignalMeasurement() and Icu_StopSignalMeasurement() from the code. */
#define ICU_SIGNAL_MEASUREMENT_API          (STD_ON)

/* Adds / removes all services related to the timestamping functionality as listed below from the code: Icu_StartTimestamp(), Icu_StopTimestamp(), Icu_GetTimestampIndex(). */
#define ICU_TIMESTAMP_API                   (STD_ON)

/* Adds / removes the service Icu_CheckWakeup() from the code. */
#define ICU_WAKEUP_FUNCTIONALITY_API        (STD_ON)

/* Adds / removes the services Icu_CheckChannelStatus() from the code. */
#define ICU_SAFETY_FUNCTION_API             (STD_ON)

/* Adds / removes the service Icu_GetInputLevel() from the code. */
#define ICU_GET_INPUT_LEVEL_API             (STD_ON)

/* If IcuEnableNotiApiCapableWakeup is true, API Icu_EnableNotification will enable associated notification */
/* for both interrupt and wakeup independently of CPU mode, else API Icu_EnableNotification will be AUTOSAR standard behavior (only associated notification interrupt).  */
#define ICU_ENABLE_NOTIAPI_CAPABLE_WAKEUP   (STD_OFF)

/* If IcuDisableNotiApiCapableWakeup is true, API Icu_DisableNotification will disable associated notification */
/* for both interrupt and wakeup independently of CPU mode, else API Icu_DisableNotification will be AUTOSAR standard behavior (only associated notification interrupt). */
#define ICU_DISABLE_NOTIAPI_CAPABLE_WAKEUP   (STD_OFF)

/* Adds / removes the service Icu_SetPrescaler() from the code. */
#define ICU_SET_PRESCALER_API                (STD_ON)

/* Adds / removes the service Icu_StartGroupTrigger() and Icu_StopGroupTrigger() from the code. */
#define ICU_CHANNEL_GROUP_API                (STD_ON)

/* Acceptance of Wakeup signal during SLEEP transition processing with Icu_SetMode. */
#define ICU_WAKEUP_ACCEPTANCE_IN_SETMODE     (TRUE)

/** This parameter defines the existence and the name of a callout function that is
 called each time an error was detected.
 The function has to have the following parameters: (ModuleId: uint16,
 InstanceId: uint8, ApiId: uint8, ErrorId: uint8) */

#define ICU_ERROR_CALLOUT_FUNCTION (Det_ReportError)


/* \brief additional derivative dependent ICU channel configuration values */
#define IcuConf_IcuChannel_IcuChannel_IGN1  (0u)
#define IcuConf_IcuChannel_IcuChannel_BCAN_RX  (1u)
#define IcuConf_IcuChannel_IcuChannel_LCAN_RX  (2u)


/* Switch for enabling Wakeup source reporting. */
#define ICU_REPORT_WAKEUP_SOURCE        (STD_OFF)

#define ICU_RESOUCRCE_TCPWM_ENABLE      (STD_OFF)

#define ICU_DMA_FUNCTION_ENABLE         (STD_OFF)


